importPackage(Packages.java.io);
importPackage(Packages.java.util);
importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{
    // get the content.body as a String
    var body = msg.get("content.body");
    var os = new ByteArrayOutputStream();
    body.write(os, 4);
    var iniFile = os.toString().replaceAll("\r", "");

    // get the [AVV-ZIP] section
    var avBegin = iniFile.substring(iniFile.indexOf("[AVV-ZIP]") + 9);
    var avZipSection  = avBegin.substring(0, avBegin.indexOf("["));

    // loop through the lines and parse each line
    var avZipMap = new HashMap();
    var lines = avZipSection.split("\n");
    for (var i=0; i<lines.length; i++) {
        var line = lines[i];
        var fields = line.split("=");
        if (fields.length >0) {
            avZipMap.put(fields[0], fields[1]);
        }
    }
    Trace.debug("FileName: " + avZipMap.get("FileName"));
    Trace.debug("MD5: " + avZipMap.get("MD5"));

    // get the [AVV-MD5] section
    var avBegin = iniFile.substring(iniFile.indexOf("[AVV-MD5]") + 9);
    var avMd5Section  = avBegin.substring(0, avBegin.indexOf("["));

    // loop through the lines and parse each
    var avMd5Map = new HashMap();
    var lines = avMd5Section.split("\n");
    for (var i=0; i<lines.length; i++) {
        var line = lines[i];
        var fields = line.split(" ");
        if (fields.length >1) {
            avMd5Map.put(fields[1], fields[0]);
        }
    }
    Trace.debug("avvclean.dat: " + avMd5Map.get("avvclean.dat"));

    msg.put("av.zipsection.map", avZipMap);
    msg.put("av.zipsection.text", avZipSection);
    msg.put("av.zipsection.datversion", avZipMap.get("DATVersion"));
    msg.put("av.zipsection.filename", avZipMap.get("FileName"));
    msg.put("av.zipsection.md5", avZipMap.get("MD5"));

    msg.put("av.md5section.map", avMd5Map);
    msg.put("av.md5section.text", avMd5Section);
    msg.put("av.md5section.avvclean", avMd5Map.get("avvclean.dat"));
    msg.put("av.md5section.avvnames", avMd5Map.get("avvnames.dat")); 
    msg.put("av.md5section.avvscan", avMd5Map.get("avvscan.dat"));

    Trace.debug("message.key: " + avZipMap.get("DATVersion") + "|" + avZipMap.get("MD5"));
    msg.put("message.key", avZipMap.get("DATVersion") + "|" + avZipMap.get("MD5"));

    return true;
}

importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{
    var md5ExecValue = msg.get('exec.output');
    var md5Value = msg.get('av.zipsection.md5');

    md5ExecValue = md5ExecValue.substring(0,md5ExecValue.indexOf(" "));

    Trace.debug("Zip MD5: " + md5ExecValue);
    Trace.debug("Expected MD5: " + md5Value);

    msg.put("zipfile.md5", md5ExecValue);

    if (md5ExecValue.equals(md5Value)) {
        return true;
    }
    else {
        msg.put("exception", "The DAT file MD5 value does not match the INI file.");
        return false;
    }
}

