importPackage(Packages.com.vordel.trace);

function invoke(msg)         
{
    var exception = msg.get("exception");

    if (exception == null) {
        circuitException = msg.get("circuit.exception");
        var text = circuitException.getMessage();
        msg.put("exception", text);
    }

    return true;         
}

